            'Multisites::Ajax' => '::',
            'Multisites:ItemListBlock:' => 'Block title::',
            'Multisites:Site:' => 'Site ID::',
            'Multisites:Template:Site' => 'Template ID:Site ID:',
            'Multisites:Project:Site' => 'Project ID:Site ID:',
            'Multisites:Template:' => 'Template ID::',
            'Multisites:Project:Template' => 'Project ID:Template ID:',
            'Multisites:SiteExtension:' => 'SiteExtension ID::',
            'Multisites:Site:SiteExtension' => 'Site ID:SiteExtension ID:',
            'Multisites:Project:' => 'Project ID::',
        );
        // DEBUG: permission schema aspect ends


        return $meta;
    }

