/**
 * Repository class used to implement own convenience methods for performing certain DQL queries.
 *
 * This is the base repository class for the many to many relationship
 * between project and template entities.
 */
class Multisites_Entity_Repository_Base_ProjectTemplate extends EntityRepository
{
    public function truncateTable()
    {
        $qb = $this->getEntityManager()->createQueryBuilder();
        $qb->delete('Multisites_Entity_ProjectTemplate', 'tbl');
        $query = $qb->getQuery();
        $query->execute();
    }
}
