/**
 * Version information implementation class.
 */
class Multisites_Version extends Multisites_Base_Version
{
    /**
     * Retrieves meta data information for this application.
     *
     * @return array List of meta data.
     */
    public function getMetaData()
    {
        $meta = parent::getMetaData();

        $meta['description'] = $this->__('Zikula Multisites module');
        $meta['displayname'] = $this->__('Multisites manager');

        return $meta;
    }
}
