namespace Zikula\MultisitesModule\Controller\Base;

use Symfony\Component\HttpFoundation\Request;
use Symfony\Component\Security\Core\Exception\AccessDeniedException;
use ModUtil;
use RuntimeException;
use ZLanguage;
use Zikula\Core\Controller\AbstractController;
use Zikula\Core\RouteUrl;
use Zikula\Core\Response\PlainResponse;

/**
 * config controller class.
 */
class ConfigController extends AbstractController
{

    /**
     * This is a custom action.
     *
     * @Theme("admin")
     *
     * @param Request  $request      Current request instance
     *
     * @return mixed Output
     *
     * @throws AccessDeniedException Thrown if the user doesn't have required permissions
     */
    public function configAction(Request $request)
    {
        $controllerHelper = $this->get('zikula_multisites_module.controller_helper');
        
        // parameter specifying which type of objects we are treating
        $objectType = $request->query->getAlnum('ot', 'site');
        $utilArgs = ['controller' => 'config', 'action' => 'config'];
        if (!in_array($objectType, $controllerHelper->getObjectTypes('controllerAction', $utilArgs))) {
            $objectType = $controllerHelper->getDefaultObjectType('controllerAction', $utilArgs);
        }
        $permLevel = ACCESS_OVERVIEW;
        if (!$this->hasPermission($this->name . ':' . ucfirst($objectType) . ':', '::', $permLevel)) {
            throw new AccessDeniedException();
        }
        /** TODO: custom logic */
        
        // return template
        return $this->render('@ZikulaMultisitesModule/Config/config.html.twig');
    }

}
