    /**
     * Pre-Process the data prior a delete operation.
     * The event happens before the entity managers remove operation is executed for this entity.
     *
     * Restrictions:
     *     - no access to entity manager or unit of work apis
     *     - will not be called for a DQL DELETE statement
     *
     * @see Zikula\MultisitesModule\Entity\SiteEntity::preRemoveCallback()
     * @return boolean true if completed successfully else false.
     *
     * @throws RuntimeException Thrown if workflow deletion fails
     */
    protected function performPreRemoveCallback()
    {
        $serviceManager = ServiceUtil::getManager();
        $dispatcher = $serviceManager->get('event_dispatcher');
    
        // create the new FilterSiteEvent and dispatch it
        $event = new FilterSiteEvent($this);
        $dispatcher->dispatch(MultisitesEvents::SITE_PRE_REMOVE, $event);
        if ($event->isPropagationStopped()) {
            return false;
        }
    
        // delete workflow for this entity
        $serviceManager = ServiceUtil::getManager();
        $workflowHelper = $serviceManager->get('zikulamultisitesmodule.workflow_helper');
        $workflowHelper->normaliseWorkflowData($this);
        $workflow = $this['__WORKFLOW__'];
        if ($workflow['id'] > 0) {
            $entityManager = $serviceManager->get('doctrine.entitymanager');
            $result = true;
            try {
                $workflow = $entityManager->find('Zikula\Core\Doctrine\Entity\WorkflowEntity', $workflow['id']);
                $entityManager->remove($workflow);
                $entityManager->flush();
            } catch (\Exception $e) {
                $result = false;
            }
            if ($result === false) {
                $session = $serviceManager->get('session');
                $session->getFlashBag()->add(\Zikula_Session::MESSAGE_ERROR, $serviceManager->get('translator')->__('Error! Could not remove stored workflow. Deletion has been aborted.'));
    
                return false;
            }
        }
    
        return true;
    }
    
    /**
     * Post-Process the data after a delete.
     * The event happens after the entity has been deleted.
     * Will be called after the database delete operations.
     *
     * Restrictions:
     *     - no access to entity manager or unit of work apis
     *     - will not be called for a DQL DELETE statement
     *
     * @see Zikula\MultisitesModule\Entity\SiteEntity::postRemoveCallback()
     * @return boolean true if completed successfully else false.
     */
    protected function performPostRemoveCallback()
    {
        $serviceManager = ServiceUtil::getManager();
    
        $objectId = $this->createCompositeIdentifier();
    
        // retrieve the upload handler
        $uploadManager = $serviceManager->get('zikulamultisitesmodule.upload_handler');
    
        $uploadFields = ['logo', 'favIcon', 'parametersCsvFile'];
        foreach ($uploadFields as $uploadField) {
            if (empty($this->$uploadField)) {
                continue;
            }
    
            // remove upload file (and image thumbnails)
            $uploadManager->deleteUploadFile('site', $this, $uploadField, $objectId);
        }
    
        $logger = $serviceManager->get('logger');
        $logger->debug('{app}: User {user} removed the {entity} with id {id}.', ['app' => 'ZikulaMultisitesModule', 'user' => UserUtil::getVar('uname'), 'entity' => 'site', 'id' => $objectId]);
    
        $dispatcher = $serviceManager->get('event_dispatcher');
    
        // create the new FilterSiteEvent and dispatch it
        $event = new FilterSiteEvent($this);
        $dispatcher->dispatch(MultisitesEvents::SITE_POST_REMOVE, $event);
    
        return true;
    }
    
