    public function __construct()
    {
        
        $serviceManager = ServiceUtil::getManager();
        $listHelper = $serviceManager->get('zikulamultisitesmodule.listentries_helper');
        
        $items = [];
        $listEntries = $listHelper->getExtensionTypeEntriesForSiteExtension();
