    /**
     * Collect available actions for this entity.
     */
    protected function prepareItemActions()
    {
        if (!empty($this->_actions)) {
            return;
        }
    
        $currentLegacyControllerType = FormUtil::getPassedValue('lct', 'user', 'GETPOST', FILTER_SANITIZE_STRING);
        $currentFunc = FormUtil::getPassedValue('func', 'index', 'GETPOST', FILTER_SANITIZE_STRING);
        $component = 'ZikulaMultisitesModule:Template:';
        $instance = $this->id . '::';
        $serviceManager = ServiceUtil::getManager();
        $permissionHelper = $serviceManager->get('zikula_permissions_module.api.permission');
        $translator = $serviceManager->get('translator');
        if ($currentLegacyControllerType == 'admin') {
            if (in_array($currentFunc, ['index', 'view'])) {
            }
            if (in_array($currentFunc, ['index', 'view', 'display'])) {
                if ($permissionHelper->hasPermission($component, $instance, ACCESS_EDIT)) {
                    $this->_actions[] = [
                        'url' => ['type' => 'template', 'func' => 'adminedit', 'arguments' => ['id' => $this['id']]],
                        'icon' => 'pencil-square-o',
                        'linkTitle' => $translator->__('Edit'),
                        'linkText' => $translator->__('Edit')
                    ];
                    $this->_actions[] = [
                        'url' => ['type' => 'template', 'func' => 'adminedit', 'arguments' => ['astemplate' => $this['id']]],
                        'icon' => 'files-o',
                        'linkTitle' => $translator->__('Reuse for new item'),
                        'linkText' => $translator->__('Reuse')
                    ];
                }
                if ($permissionHelper->hasPermission($component, $instance, ACCESS_DELETE)) {
                    $this->_actions[] = [
                        'url' => ['type' => 'template', 'func' => 'admindelete', 'arguments' => ['id' => $this['id']]],
                        'icon' => 'trash-o',
                        'linkTitle' => $translator->__('Delete'),
                        'linkText' => $translator->__('Delete')
                    ];
                }
            }
            
            // more actions for adding new related items
            $authAdmin = $permissionHelper->hasPermission($component, $instance, ACCESS_ADMIN);
            
            $uid = UserUtil::getVar('uid');
            if ($authAdmin || (isset($uid) && isset($this->createdUserId) && $this->createdUserId == $uid)) {
            
                $urlArgs = ['template' => $this->id];
                if ($currentFunc == 'view') {
                    $urlArgs['returnTo'] = 'adminViewTemplate';
                } elseif ($currentFunc == 'display') {
                    $urlArgs['returnTo'] = 'adminDisplayTemplate';
                }
                $this->_actions[] = [
                    'url' => ['type' => 'site', 'func' => 'adminedit', 'arguments' => $urlArgs],
                    'icon' => 'plus',
                    'linkTitle' => $translator->__('Create site'),
                    'linkText' => $translator->__('Create site')
                ];
            
                $urlArgs = ['templates' => $this->id];
                if ($currentFunc == 'view') {
                    $urlArgs['returnTo'] = 'adminViewTemplate';
                } elseif ($currentFunc == 'display') {
                    $urlArgs['returnTo'] = 'adminDisplayTemplate';
                }
                $this->_actions[] = [
                    'url' => ['type' => 'project', 'func' => 'adminedit', 'arguments' => $urlArgs],
                    'icon' => 'plus',
                    'linkTitle' => $translator->__('Create project'),
                    'linkText' => $translator->__('Create project')
                ];
            }
        }
    }
    
