namespace Zikula\MultisitesModule\Entity\Repository\Base;

use Doctrine\ORM\EntityRepository;
use ServiceUtil;
use UserUtil;

/**
 * Repository class used to implement own convenience methods for performing certain DQL queries.
 *
 * This is the base repository class for the many to many relationship
 * between project and template entities.
 */
class ProjectTemplate extends EntityRepository
{
    public function truncateTable()
    {
        $qb = $this->getEntityManager()->createQueryBuilder();
        $qb->delete('\\Zikula\\MultisitesModule\\Entity\\ProjectTemplate', 'tbl');
        $query = $qb->getQuery();
        $query->execute();

        $serviceManager = ServiceUtil::getManager();
        $logger = $serviceManager->get('logger');
        $logger->debug('{app}: User {user} truncated the {entity} entity table.', ['app' => 'ZikulaMultisitesModule', 'user' => UserUtil::getVar('uname'), 'entity' => 'project template']);
    }
}
