namespace Zikula\MultisitesModule\Entity\Repository;

use Zikula\MultisitesModule\Entity\Repository\Base\AbstractProjectRepository;

use Doctrine\ORM\QueryBuilder;
use ModUtil;

/**
 * Repository class used to implement own convenience methods for performing certain DQL queries.
 *
 * This is the concrete repository class for project entities.
 */
class ProjectRepository extends AbstractProjectRepository
{
    /**
     * {@inheritdoc}
     */
    protected function getViewQuickNavParameters($context = '', $args = [])
    {
        $parameters = parent::getViewQuickNavParameters($context, $args);

        $parameters['templates'] = $this->request->query->getInt('templates', 0);

        return $parameters;
    }

    /**
     * {@inheritdoc}
     */
    public function addCommonViewFilters(QueryBuilder $qb)
    {
        $currentFunc = $this->request->query->getAlpha('func', 'index');
        if ($currentFunc == 'edit') {
            return $qb;
        }

        $parameters = $this->getViewQuickNavParameters('', []);
        foreach ($parameters as $k => $v) {
            if ($k == 'templates') {
                if ($v > 0 && false !== strpos($qb->getDql(), 'tblTemplates')) {
                    $qb->andWhere(':templates MEMBER OF tbl.templates')
                       ->setParameter('templates', $v);
                }
            } elseif (in_array($k, array('q', 'searchterm'))) {
                // quick search
                if (!empty($v)) {
                    $qb = $this->addSearchFilter($qb, $v);
                }
            } else if (!is_array($v)) {
                // field filter
                if ((!is_numeric($v) && $v != '') || (is_numeric($v) && $v > 0)) {
                    if ($k == 'workflowState' && substr($v, 0, 1) == '!') {
                        $qb->andWhere('tbl.' . $k . ' != :' . $k)
                           ->setParameter($k, substr($v, 1, strlen($v)-1));
                    } elseif (substr($v, 0, 1) == '%') {
                        $qb->andWhere('tbl.' . $k . ' LIKE :' . $k)
                           ->setParameter($k, '%' . $v . '%');
                    } else {
                        $qb->andWhere('tbl.' . $k . ' = :' . $k)
                           ->setParameter($k, $v);
                   }
                }
            }
        }
    
        $qb = $this->applyDefaultFilters($qb, $parameters);

        return $qb;
    }

    /**
     * {@inheritdoc}
     */
    protected function addJoinsToSelection()
    {
        $selection = ', tblTemplates';//', tblSites, tblTemplates';
    
        return $selection;
    }
    
    /**
     * {@inheritdoc}
     */
    protected function addJoinsToFrom(QueryBuilder $qb)
    {
        //$qb->leftJoin('tbl.sites', 'tblSites');
        $qb->leftJoin('tbl.templates', 'tblTemplates');
    
        return $qb;
    }
}
