namespace Zikula\MultisitesModule\Form;

use Zikula\MultisitesModule\Form\Base\AppSettingsType as BaseAppSettingsType;

use Symfony\Component\Form\FormBuilderInterface;

/**
 * Configuration form type implementation class.
 */
class AppSettingsType extends BaseAppSettingsType
{
    /**
     * Adds fields for security settings fields.
     *
     * @param FormBuilderInterface $builder The form builder.
     * @param array                $options The options.
     */
    public function addSecuritySettingsFields(FormBuilderInterface $builder, array $options)
    {
        $builder
            ->add('globalAdminPassword', 'Symfony\Component\Form\Extension\Core\Type\PasswordType', [
                'label' => $this->__('Global admin password') . ':',
                'required' => false,
                'data' => $this->modVars['globalAdminPassword'],
                'empty_data' => '',
                'attr' => [
                    'title' => $this->__('Enter the global admin password.')
                ],
                'max_length' => 255
            ])
        ;
    }
}
