namespace Zikula\MultisitesModule\Form\Base;

use Symfony\Component\Form\AbstractType;
use Symfony\Component\Form\FormBuilderInterface;
use Zikula\Common\Translator\TranslatorInterface;
use Zikula\Common\Translator\TranslatorTrait;
use Zikula\ExtensionsModule\Api\VariableApi;

/**
 * Configuration form type base class.
 */
abstract class AbstractAppSettingsType extends AbstractType
{
    use TranslatorTrait;

    /**
     * @var VariableApi
     */
    protected $variableApi;

    /**
     * @var array
     */
    protected $modVars;

    /**
     * AppSettingsType constructor.
     *
     * @param TranslatorInterface $translator  Translator service instance
     * @param VariableApi         $variableApi VariableApi service instance
     */
    public function __construct(TranslatorInterface $translator, VariableApi $variableApi)
    {
        $this->setTranslator($translator);
        $this->variableApi = $variableApi;
        $this->modVars = $this->variableApi->getAll('ZikulaMultisitesModule');
    }

    /**
     * Sets the translator.
     *
     * @param TranslatorInterface $translator Translator service instance
     */
    public function setTranslator(/*TranslatorInterface */$translator)
    {
        $this->translator = $translator;
    }

    /**
     * {@inheritdoc}
     */
    public function buildForm(FormBuilderInterface $builder, array $options)
    {
        $this->addGeneralFields($builder, $options);
        $this->addSecuritySettingsFields($builder, $options);
        $this->addImagesFields($builder, $options);

        $builder
            ->add('save', 'Symfony\Component\Form\Extension\Core\Type\SubmitType', [
                'label' => $this->__('Update configuration'),
                'icon' => 'fa-check',
                'attr' => [
                    'class' => 'btn btn-success'
                ]
            ])
            ->add('cancel', 'Symfony\Component\Form\Extension\Core\Type\SubmitType', [
                'label' => $this->__('Cancel'),
                'icon' => 'fa-times',
                'attr' => [
                    'class' => 'btn btn-default',
                    'formnovalidate' => 'formnovalidate'
                ]
            ])
        ;
    }

    /**
     * Adds fields for general fields.
     *
     * @param FormBuilderInterface $builder The form builder
     * @param array                $options The options
     */
    public function addGeneralFields(FormBuilderInterface $builder, array $options)
    {
        $builder
            ->add('tempAccessFileContent', 'Symfony\Component\Form\Extension\Core\Type\TextType', [
                'label' => $this->__('Temp access file content') . ':',
                'required' => false,
                'data' => $this->modVars['tempAccessFileContent'],
                'empty_data' => '',
                'attr' => [
                    'title' => $this->__('Enter the temp access file content.')
                ],'max_length' => 255
            ])
        ;
    }

    /**
     * Adds fields for security settings fields.
     *
     * @param FormBuilderInterface $builder The form builder
     * @param array                $options The options
     */
    public function addSecuritySettingsFields(FormBuilderInterface $builder, array $options)
    {
        $builder
            ->add('globalAdminName', 'Symfony\Component\Form\Extension\Core\Type\TextType', [
                'label' => $this->__('Global admin name') . ':',
                'required' => false,
                'data' => $this->modVars['globalAdminName'],
                'empty_data' => '',
                'attr' => [
                    'title' => $this->__('Enter the global admin name.')
                ],'max_length' => 255
            ])
            ->add('globalAdminPassword', 'Symfony\Component\Form\Extension\Core\Type\TextType', [
                'label' => $this->__('Global admin password') . ':',
                'required' => false,
                'data' => $this->modVars['globalAdminPassword'],
                'empty_data' => '',
                'attr' => [
                    'title' => $this->__('Enter the global admin password.')
                ],'max_length' => 255
            ])
            ->add('globalAdminEmail', 'Symfony\Component\Form\Extension\Core\Type\TextType', [
                'label' => $this->__('Global admin email') . ':',
                'required' => false,
                'data' => $this->modVars['globalAdminEmail'],
                'empty_data' => '',
                'attr' => [
                    'title' => $this->__('Enter the global admin email.')
                ],'max_length' => 255
            ])
        ;
    }

    /**
     * Adds fields for images fields.
     *
     * @param FormBuilderInterface $builder The form builder
     * @param array                $options The options
     */
    public function addImagesFields(FormBuilderInterface $builder, array $options)
    {
        $builder
            ->add('enableShrinkingForSiteLogo', 'Symfony\Component\Form\Extension\Core\Type\CheckboxType', [
                'label' => $this->__('Enable shrinking for site logo') . ':',
                'label_attr' => [
                    'class' => 'zikulamultisitesmodule-form-tooltips',
                    'title' => $this->__('Whether to enable shrinking to maximum image dimensions. The original image is not stored.')
                ],
                'help' => $this->__('Whether to enable shrinking to maximum image dimensions. The original image is not stored.'),
                'required' => false,
                'data' => $this->modVars['enableShrinkingForSiteLogo'],
                'empty_data' => 'false',
                'attr' => [
                    'title' => $this->__('The enable shrinking for site logo option.')
                ],
            ])
            ->add('shrinkWidthSiteLogo', 'Symfony\Component\Form\Extension\Core\Type\IntegerType', [
                'label' => $this->__('Shrink width site logo') . ':',
                'label_attr' => [
                    'class' => 'zikulamultisitesmodule-form-tooltips',
                    'title' => $this->__('The maximum image width.')
                ],
                'help' => $this->__('The maximum image width.'),
                'required' => false,
                'data' => $this->modVars['shrinkWidthSiteLogo'],
                'empty_data' => '800',
                'attr' => [
                    'title' => $this->__('Enter the shrink width site logo. Only digits are allowed.')
                ],'max_length' => 4,
                'scale' => 0,
                'input_group' => ['right' => $this->__('pixels')]
            ])
            ->add('shrinkHeightSiteLogo', 'Symfony\Component\Form\Extension\Core\Type\IntegerType', [
                'label' => $this->__('Shrink height site logo') . ':',
                'label_attr' => [
                    'class' => 'zikulamultisitesmodule-form-tooltips',
                    'title' => $this->__('The maximum image height.')
                ],
                'help' => $this->__('The maximum image height.'),
                'required' => false,
                'data' => $this->modVars['shrinkHeightSiteLogo'],
                'empty_data' => '600',
                'attr' => [
                    'title' => $this->__('Enter the shrink height site logo. Only digits are allowed.')
                ],'max_length' => 4,
                'scale' => 0,
                'input_group' => ['right' => $this->__('pixels')]
            ])
        ;
    }

    /**
     * {@inheritdoc}
     */
    public function getBlockPrefix()
    {
        return 'zikulamultisitesmodule_appsettings';
    }

    /**
     * {@inheritdoc}
     */
    public function getName()
    {
        return $this->getBlockPrefix();
    }
}
