/**
 * Delete operation.
 * @param object $entity The treated object.
 * @param array  $params Additional arguments.
 *
 * @return bool False on failure or true if everything worked well.
 *
 * @throws RuntimeException Thrown if executing the workflow action fails
 */
function ZikulaMultisitesModule_operation_delete(&$entity, $params)
{
    // get entity manager
    $serviceManager = \ServiceUtil::getManager();
    $entityManager = $serviceManager->get('doctrine.entitymanager');
    $logger = $serviceManager->get('logger');
    
    // delete entity
    try {
        $entityManager->remove($entity);
        $entityManager->flush();
        $result = true;
        $logger->notice('{app}: User {user} deleted an entity.', ['app' => 'ZikulaMultisitesModule', 'user' => \UserUtil::getVar('uname')]);
    } catch (\Exception $e) {
        $logger->error('{app}: User {user} tried to delete an entity, but failed.', ['app' => 'ZikulaMultisitesModule', 'user' => \UserUtil::getVar('uname')]);
        throw new \RuntimeException($e->getMessage());
    }

    // return result of this operation
    return $result;
}
